mapper.select:
select A.BASE_YEAR_MONTH as baseYearMonth, A.BASE_SALARY_TTL as baseSalary, A.OVERTIME_SALARY_TTL as overtimeSalary, A.NIGHT_SALARY_TTL as nightSalary, A.HOLIDAY_SALARY_TTL as holidaySalary, A.CARE_SALARY_TTL as careSalary, A.REWARD_SALARY_TTL as rewardSalary, A.MEAL_TTL as mealSalary, A.BUSINESS_SALARY_TTL as businessSalary, A.INCOME_TAX_TTL as incomeTax, A.RESIDENCE_TAX_TTL as residenceTax, A.PENSION_TTL as pension, A.HEALTH_INSURANCE_TTL as healthInsurance, A.LONGTERM_INSURANCE_TTL as longtermInsurance, A.EMPLOYMENT_INSURANCE_TTL as employmentInsurance, A.START_DATE as startDate, A.END_DATE as endDate, A.PAY_DATE as payDate, B.EMPLOYEE_NO as employeeNo, B.EMPLOYEE_NAME as name, B.HIRE_DATE as hireDate, B.RETIREMENT_DATE as retirementDate, C.DEPT_NAME as deptName, D.POSITION_NAME as positionName from (select * from SALARY_HISTORIES
where BASE_YEAR_MONTH = #{basemonth}
) A , EMPLOYEES B, DEPARTMENTS C, POSITIONS D
A.EMPLOYEE_NO(+) = B.EMPLOYEE_NO and B.DEPT_NO = C.DEPT_NO and B.POSITION_NO = D.POSITION_NO
and A.START_DATE = #{startdate}
and A.END_DATE = #{enddate}
and A.PAY_DATE = #{paydate}
and B.EMPLOYEE_STATUS = 'N'
order by B.EMPLOYEE_NO asc

mapper.delete:
	delete from 
		SALARY_HISTORIES
 	where
 		EMPLOYEE_NO = #{employeeNo}
		and BASE_YEAR_MONTH = #{baseYearMonth}

mapper.select:
select B.EMPLOYEE_NO as employeeNo, B.EMPLOYEE_NAME as name, SUM(A.BASE_SALARY_TTL + A.OVERTIME_SALARY_TTL + A.NIGHT_SALARY_TTL + A.HOLIDAY_SALARY_TTL + A.CARE_SALARY_TTL + A.REWARD_SALARY_TTL + A.MEAL_TTL + A.BUSINESS_SALARY_TTL) as totalSalary, SUM(A.INCOME_TAX_TTL + A.RESIDENCE_TAX_TTL + A.PENSION_TTL + A.HEALTH_INSURANCE_TTL + A.LONGTERM_INSURANCE_TTL + A.EMPLOYMENT_INSURANCE_TTL) as deductionSalary, SUM(A.BASE_SALARY_TTL + A.OVERTIME_SALARY_TTL + A.NIGHT_SALARY_TTL + A.HOLIDAY_SALARY_TTL + A.CARE_SALARY_TTL + A.REWARD_SALARY_TTL + A.MEAL_TTL + A.BUSINESS_SALARY_TTL) - SUM(A.INCOME_TAX_TTL + A.RESIDENCE_TAX_TTL + A.PENSION_TTL + A.HEALTH_INSURANCE_TTL + A.LONGTERM_INSURANCE_TTL + A.EMPLOYMENT_INSURANCE_TTL) as realSalary from SALARY_HISTORIES A , EMPLOYEES B
A.EMPLOYEE_NO = B.EMPLOYEE_NO
and A.PAY_DATE >= #{startdate}
and A.PAY_DATE <= #{enddate}
group by B.EMPLOYEE_NO, B.EMPLOYEE_NAME
